import './App.css';
import React from 'react';


class App extends React.Component {
  handleButtonOne() {
    console.log('Primeiro clique');
  }

  handleButtonTwo() {
    console.log('Segundo clique');
  }

  handleButtonThree() {
    console.log('Terceiro clique');
  }
  render() {
    return (
      <div>
        <button onClick={ this.handleButtonOne }>Botão 1</button>
        <button onClick={ handleButtonTwo }>Botão 2</button>
        <button onClick={ handleButtonThree }>Botão 3</button>
      </div>
    );
  }
}

export default App;
