import React, { Component } from "react";

class Form extends Component {
  constructor() {
    super();

    this.state = {
      techs: 'React',
      idade: 18,
      confirmarPresenca: false,
      informacoes: '',
    }
  }

  handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({ [name]: value});
  }

  render() {
    return (
      <div className="form-container">
        <h1>Meu Formulário React</h1>

        <form className="form">
          <fieldset className='fieldset'>
            <Techs value={this.state.techs} handleChange={this.h}/>


            <label>Qual a sua idade?
              <input 
                type='number'
                name='idade'
                value={this.state.idade}
                onChange={this.handleChange}
                />
            </label>

            <label>Confirmar presença
              <input
                type='checkbox'
                name='confirmarPresenca'
                value={this.state.confirmarPresenca}
                onChange={this.handleChange}
              />
            </label>

            <fieldset className='infos'>
              <label>Informações adicionais:
                <textarea 
                  name='informacoes'
                  value={this.state.informacoes} 
                  onChange={this.handleChange}
                />
              </label>

              <label>Upload de arquivo: 
                <input 
                  name='file'
                  type='file'            
                />
              </label>
            </fieldset>
          </fieldset>
        </form>
      </div>
    )
  }
}

export default Form;
