import React, { Component } from 'react';
import './App.css';
import pokemons from './data';
import Pokedex from './Pokedex';
import { BrowserRouter, Route, Switch } from 'react-router-dom';

class App extends Component {
  render() {
    return (
      <>
        <BrowserRouter>
          <Switch>
            <Route exact path='/'
            render={Pokedex} />
          </Switch>
        </BrowserRouter>

        <div className="App">
          <h1> Pokedex </h1>


          <Pokedex pokemons={pokemons} />
        </div>
      </>  
    );
  }
}

export default App;
